var calculateMonthlyPayment=function(e,t,n){if(n)var a=n/100/12;var l=e*a/(1-Math.pow(1/(1+a),12*t));return{principal:e,years:t,rate:n,monthlyPayment:l,monthlyRate:a}};document.getElementById("submit").addEventListener("click",function(){var e=document.getElementById("principal").value,t=document.getElementById("years").value,n=document.getElementById("rate").value,a=(l=calculateMonthlyPayment(e,t,n)).monthlyPayment,l=l.monthlyRate;document.getElementById("Payment").innerHtml=a});